package common

import (
	"encoding/json"
)

// ToJSON marshals the specified object and returns the resulting JSON string
func ToJSON(obj interface{}) string {
	b, err := json.MarshalIndent(obj, "", "  ")
	if err != nil {
		panic(err)
	}
	return string(b)
}
